import React, { useEffect, useState, useContext } from "react";
import "../index.css"
import { ContextGlobal } from './utils/global.context'
import { Link } from "react-router-dom";


const Card = ({ name, username, id }) => {



  const {theme}= useContext(ContextGlobal)

  const favoritesFromStorage = JSON.parse(localStorage.getItem('favorites')) || [];
  
  const [isFavorite, setIsFavorite] = useState(false);
  const [favorites, setFavorites] = useState(favoritesFromStorage)
  
 // const isAlreadyFav = favorites.some(fav => fav.id === id);
  

  const addFav = ()=>{
    // Aqui iria la logica para agregar la Card en el localStorage
    setFavorites(...favorites,{name,username,id})
    localStorage.setItem('favorites', JSON.stringify(favorites));
    setIsFavorite(true)
    // if (!isAlreadyFav) {
    //   setFavorites(...favorites,{name,username,id})
    //   //favorites.push({ name, username, id });
    //   localStorage.setItem('favorites', JSON.stringify(favorites));
    //   setIsFavorite(true)
    // }
  }
const clearFav = () =>{
  setIsFavorite(false)
  localStorage.removeItem({ name, username, id });
  setFavorites(favorites.pop())
}
function handleFav(){
  if(!isFavorite){
    addFav()
  }else{
    clearFav()
  }
}
  // useEffect(() => {
  //   if(favorites.some(fav => fav.id === id)){
  //     setIsFavorite(true)
  //   };
  // }, [favorites, id]);
  return (
    <div className= {`card ${theme}`}>
      <Link to={`/dentist/${id}`}>
        {/* En cada card deberan mostrar en name - username y el id */}

        {/* No debes olvidar que la Card a su vez servira como Link hacia la pagina de detalle */}

        {/* Ademas deberan integrar la logica para guardar cada Card en el localStorage */}

    {/* <img src="/images/doctor.jpg"
          alt="doctor placeholder"/>
       */}
       <h1>{name}</h1>
       <h2>{username}</h2>
       <h3>{id}</h3>
       </Link>
       <button onClick={handleFav} className={`favButton ${isFavorite ? 'favorite' : ''}`}>
  {isFavorite ? 'Added to favorites ⭐' : 'Add to favorites'}
</button>
    </div>
  );
};

export default Card;
